import {CREATE_ROOM} from "../actions";

const initialState = {
    room: [
        {
            id: 0,
            host: 0,
            game: 0
        }
    ]
}

const roomState = (state = initialState, action) => {
    switch (action.type) {
        case CREATE_ROOM:
            state.id++;
            state.host++;
            state.game++;
            return {
                ...state,

                    room: [
                        {
                            id: ++state.id,
                            host: state.host,
                            game: state.game
                        }
                    ]

            }

        default:
            return state;
    }
}

export default roomState;
