import {CREATE_ROOM} from "../actions";
import React from 'react';
import store from '../store';
let st = store.getState();

const initialState = {
    room: 99,
    number: 100
}

const createRoomButton = (state = initialState, action) => {
    switch (action.type) {
        case CREATE_ROOM:
            //state.room++;
            st.room++
            console.log(state)
            return state.room;

        default:
            return state;
    }
}

export default createRoomButton;
