import {CREATE_ROOM} from "../actions";

const initialState = {
    room: []
}

const roomState = (state = [], action) => {
    switch (action.type) {
        case CREATE_ROOM:

            return [
                ...state,
                        {
                            id: 1,
                            host: 1,
                            game: 1
                        }
                    ]
            return [...state, {id: action.id, text: action.text, completed: false, status: 0}];            

        default:
            return state;
    }
}

export default roomState;
