import {CREATE_ROOM} from "../actions";

const initialState = {
    room: []
}

const roomState = (state = initialState, action) => {
    switch (action.type) {
        case CREATE_ROOM:
            console.log(state)
            return {
                ...state,
                id: ++state.id,
                host: ++state.host,
                game: ++state.game
            }

        default:
            return {
                ...state
            };
    }
}

export default roomState;
