import { CREATE_ROOM } from "../actions";

const initialState = {
    id: 0,
    name: 'ルーム番号',
    room: []
}

const testReducer = (state = initialState, action) => {
    switch (action.type) {
        case CREATE_ROOM:
            return {
                ...state,
                id: ++state.id,
                room: [
                    ...state.room,
                    {
                        key: state.id,
                        name: state.name
                    }
                ]
            };
        default:
            return state;
    }
    console.log(state)
}

export default testReducer;
