import {CREATE_ROOM} from "../actions";

const initialState = {
    room: 0
}

const roomState = (state = initialState, action) => {
    switch (action.type) {
        case CREATE_ROOM:
            const num = state.room++;

            return {
                ...state,
                room: num,
              };

        default:
            return {
                ...state
            };
    }
}

export default roomState;
