import {CREATE_ROOM} from "../actions";

const initialState = {
    room: 0
}

const roomState = (state = initialState, action) => {
    switch (action.type) {
        case CREATE_ROOM:

            return {
                ...state,
                room: room++,
              };

        default:
            return state;
    }
}

export default roomState;
