import {CREATE_ROOM} from "../actions";

const initialState = {
    room: [
        {
            id: 0,
            host: 0,
            game: 0
        }
    ]
}

const roomState = (state = initialState, action) => {
    switch (action.type) {
        case CREATE_ROOM:
            console.log(state)
            return [
                ...state, {
                id: ++state.id,
                host: ++state.host,
                game: ++state.game}
                ]


        default:
            return {
                ...state
            };
    }
}

export default roomState;
